/* –– MORPHMAN COLOR CODING - Go to Google and search 'colour picker', then use hex code to change these ;)  */
[mtype=unknown] { color: #f21313; }
[mtype=seen] { color: orange; }
[mtype=known] { color: #f21313; }

#front rt {visibility: hidden;}
#front ruby:hover rt { visibility: visible;}

/* #main IS THE MAIN DISPLAY BOX
PLATFORMS: WEBKIT, WINDOWS, MAC 
  */ 

#main{
	background-color:#363c4a;
	width: 100%;
	max-width: 600px;
	padding:-0px;
	border-radius: 15px;
	box-shadow: 0px 0px 10px 1px rgba(0,0,0,0.75);
	-webkit-box-shadow: 0px 0px 10px 1px rgba(0,0,0,0.75);
	-moz-box-shadow: 0px 0px 10px 1px rgba(0,0,0,0.75);
	overflow:scroll;
	display: -webkit-box;
	-webkit-box-align: stretch;
	-webkit-box-pack: center;
	-webkit-box-orient: vertical;
}


#main::-webkit-scrollbar {
    display: none; 
}


.card {
    font-family:"ヒラギノ角ゴ Pro W3", "Hiragino Kaku Gothic Pro",Osaka, "メイリオ", Meiryo, "ＭＳ Ｐゴシック", "MS PGothic", sans-serif;
    font-size: 32px;
    text-align: center;
    color: white;
    background-color: #161921 !important;
    max-height:100%;
    display: flex;
    justify-content: center;
    align-items: center;
}

#image {
 	 text-align: center;
	overflow-y: scroll;
	max-height:250px;
	margin-bottom:-15px;
	margin-top: -5px;
	vertical-align: baseline;
}

#image::-webkit-scrollbar {
    display: none;
}

#image img {
	max-width:1000px;
	width:100%;
	border-radius: 10px 10px 0 0;
	overflow: hidden;
}

.mobile #image {
 	text-align: center;
	overflow-y: scroll;
	max-height:155px;
	margin-bottom:0px;
}

#audio {
	margin:3%;
	margin-top: 0%;
}

.card.nightMode {
background-color: #161921 !important;
}

.collapsible {
	background-color: 
	#4b556b;
	color: white;
	cursor: pointer;
	padding: 0px;
	min-height:30px;
	width: 100%;
	border: none;
	text-align: center;
	outline: none;
	font-size: 15px;	
	text-shadow: 2px 2px 2px #000000;
	border-radius: 3px;
}

.active, .collapsible:hover {
	background-color: #4b556b;
	box-shadow: 0px 0px 10px 1px rgba(0,0,0,0.2);
	-webkit-box-shadow: 0px 0px 10px 1px rgba(0,0,0,0.2);
	-moz-box-shadow: 0px 0px 10px 1px rgba(0,0,0,0.2);

}

.content {
	padding: 0 18px;
	border-radius: 0px 0px 15px 15px;
	background-color: white;
	max-height: 0;
	overflow: hidden;
	transition: max-height 0.5s ease-out;
	box-shadow: 0px 0px 10px 1px rgba(0,0,0,0.3);
}

.mobile .content {
	padding: 0 18px;
	border-radius: 0px 0px 15px 15px;
	background-color: white;
	max-height: 0;
	overflow: hidden;
	transition: max-height 0.5s ease-out;
	box-shadow: 0px 0px 10px 1px rgba(0,0,0,0.3);
	margin-bottom: 5%;
}

.container {
	position: relative;
	text-align: center;
	color: white;
}

.top-right {background-color: rgba(0, 0, 0, 1);


	font-size:10px;
	text-shadow: 2px 2px 2px #000000;
	opacity: 0.8;

}

.content img {
	border-radius: 5px;
	margin-bottom: 3%;
	box-shadow: 0px 0px 10px 1px rgba(0,0,0,0.3);
}

#Title {
	margin:3%;
	font-size:32px; 
	text-shadow: 2px 2px 4px #000000;
}

.mobile #Title {
	margin:3%;
	font-size:32px; 
	text-shadow: 2px 2px 4px #000000;
}

a:link {
	color: white;
	opacity:0.5;
	text-decoration: none;
}

A:hover {
	color: white;
	opacity: 1;
	text-decoration: none;
}
